	TITLE = #{title}
	, CONTENTS = #{contents}
	, DISPLAY_YN = #{displayYn}
	, MOD_ID = 'admin'
	, MOD_DATE = NOW()
WHERE
	BOARD_IDX = #{boardIdx}

